        //        "quantity_cumulative" => "0",
        //        "price" => "0.01",                              // only updates and snapshots
        //        "post_only" => false,
        //        "reduce_only" => false,                         // only margin and contract
        //        "display_quantity" => "0",                      // only updates and snapshot
        //        "created_at" => "2021-07-02T22:52:32.864Z",
