        #       "realizedPnl": "-0.00009534",
        #       "side": "SELL",
        #       "symbol": "BTCUSDT",
        #       "time": 1612733566708,
        #     }
        #
        # {respType: FULL}
        #
        #     {
        #       "commission": "4.00000000",
        #       "commissionAsset": "USDT",
        #       "price": "4000.00000000",
        #       "qty": "1.00000000",
        #       "tradeId": "1234",
